import React, { useState } from 'react';
import { useNavigate, Navigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import {
    Mail,
    Lock,
    Eye,
    EyeOff,
    AlertCircle,
    BookOpen,
    Users,
    Award,
    TrendingUp,
    CheckCircle,
    ArrowRight
} from 'lucide-react';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [selectedRole, setSelectedRole] = useState(null);

    const { login, isAuthenticated, user } = useAuth();
    const navigate = useNavigate();

    if (isAuthenticated && user) {
        const dashboardRoutes = {
            admin: '/admin',
            teacher: '/teacher',
            student: '/student'
        };
        return <Navigate to={dashboardRoutes[user.role]} replace />;
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const userData = await login(email, password);
            const dashboardRoutes = {
                admin: '/admin',
                teacher: '/teacher',
                student: '/student'
            };
            navigate(dashboardRoutes[userData.role]);
        } catch (err) {
            setError(err.response?.data?.message || 'Login failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const quickLogin = (role) => {
        const credentials = {
            admin: { email: 'admin@education.com', password: 'admin123' },
            teacher: { email: 'teacher@education.com', password: 'teacher123' },
            student: { email: 'student@education.com', password: 'student123' }
        };
        setEmail(credentials[role].email);
        setPassword(credentials[role].password);
        setSelectedRole(role);
    };

    const features = [
        { icon: BookOpen, text: 'Access 1000+ Courses', color: 'text-blue-400' },
        { icon: Users, text: 'Learn from Expert Teachers', color: 'text-green-400' },
        { icon: Award, text: 'Earn Certificates', color: 'text-yellow-400' },
        { icon: TrendingUp, text: 'Track Your Progress', color: 'text-purple-400' }
    ];

    return (
        <div className="min-h-screen flex">
            {/* Left Side - Hero Section */}
            <div className="hidden lg:flex lg:w-1/2 relative overflow-hidden">
                <div
                    className="absolute inset-0 bg-cover bg-center"
                    style={{
                        backgroundImage: `url('https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')`
                    }}
                />
                <div className="absolute inset-0 bg-gradient-to-br from-blue-900/90 via-purple-900/80 to-indigo-900/90" />

                <div className="relative z-10 flex flex-col justify-between p-12 text-white">
                    <div className="flex items-center gap-3">
                        <div className="w-12 h-12 bg-white/20 backdrop-blur-sm rounded-xl flex items-center justify-center">
                            <BookOpen className="w-7 h-7 text-white" />
                        </div>
                        <div>
                            <h1 className="text-2xl font-bold">SmartEdu</h1>
                            <p className="text-sm text-blue-200">Learning Platform</p>
                        </div>
                    </div>

                    <div className="space-y-8">
                        <div>
                            <h2 className="text-4xl xl:text-5xl font-bold leading-tight">
                                Unlock Your
                                <span className="block text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-400">
                                    Learning Potential
                                </span>
                            </h2>
                            <p className="mt-4 text-lg text-blue-100 max-w-md">
                                Join thousands of students and teachers on the most advanced education platform. Learn, teach, and grow together.
                            </p>
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                            {features.map((feature, index) => (
                                <div
                                    key={index}
                                    className="flex items-center gap-3 p-3 bg-white/10 backdrop-blur-sm rounded-xl"
                                >
                                    <feature.icon className={`w-5 h-5 ${feature.color}`} />
                                    <span className="text-sm font-medium">{feature.text}</span>
                                </div>
                            ))}
                        </div>

                        <div className="flex gap-8">
                            <div>
                                <p className="text-3xl font-bold">50K+</p>
                                <p className="text-sm text-blue-200">Active Students</p>
                            </div>
                            <div>
                                <p className="text-3xl font-bold">1000+</p>
                                <p className="text-sm text-blue-200">Courses</p>
                            </div>
                            <div>
                                <p className="text-3xl font-bold">500+</p>
                                <p className="text-sm text-blue-200">Expert Teachers</p>
                            </div>
                        </div>
                    </div>

                    <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-6">
                        <div className="flex items-start gap-4">
                            <img
                                src="https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&q=80"
                                alt="Student"
                                className="w-12 h-12 rounded-full object-cover border-2 border-white/30"
                            />
                            <div>
                                <p className="text-sm text-blue-100 italic">
                                    "SmartEdu transformed my learning experience. The AI chatbot helps me understand complex topics instantly!"
                                </p>
                                <p className="mt-2 font-semibold">Sarah Johnson</p>
                                <p className="text-xs text-blue-300">Computer Science Student</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Right Side - Login Form */}
            <div className="w-full lg:w-1/2 flex items-center justify-center p-6 sm:p-12 bg-gray-50">
                <div className="w-full max-w-md">
                    <div className="lg:hidden text-center mb-8">
                        <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-br from-blue-600 to-purple-600 rounded-2xl shadow-lg mb-4">
                            <BookOpen className="w-9 h-9 text-white" />
                        </div>
                        <h1 className="text-2xl font-bold text-gray-900">SmartEdu</h1>
                        <p className="text-gray-500">Learning Platform</p>
                    </div>

                    <div className="text-center mb-8">
                        <h2 className="text-3xl font-bold text-gray-900">Welcome Back!</h2>
                        <p className="text-gray-500 mt-2">Sign in to continue your learning journey</p>
                    </div>

                    <div className="mb-6">
                        <p className="text-sm text-gray-500 text-center mb-3">Quick access demo accounts</p>
                        <div className="grid grid-cols-3 gap-3">
                            {[
                                { role: 'admin', label: 'Admin', color: 'from-purple-500 to-purple-600', hoverColor: 'hover:from-purple-600 hover:to-purple-700' },
                                { role: 'teacher', label: 'Teacher', color: 'from-green-500 to-green-600', hoverColor: 'hover:from-green-600 hover:to-green-700' },
                                { role: 'student', label: 'Student', color: 'from-blue-500 to-blue-600', hoverColor: 'hover:from-blue-600 hover:to-blue-700' }
                            ].map((item) => (
                                <button
                                    key={item.role}
                                    onClick={() => quickLogin(item.role)}
                                    className={`relative p-3 rounded-xl text-white text-sm font-medium transition-all transform hover:scale-105 hover:shadow-lg bg-gradient-to-br ${item.color} ${item.hoverColor} ${selectedRole === item.role ? 'ring-2 ring-offset-2 ring-gray-400' : ''}`}
                                >
                                    {selectedRole === item.role && (
                                        <CheckCircle className="absolute -top-1 -right-1 w-5 h-5 text-white bg-green-500 rounded-full" />
                                    )}
                                    {item.label}
                                </button>
                            ))}
                        </div>
                    </div>

                    <div className="relative mb-6">
                        <div className="absolute inset-0 flex items-center">
                            <div className="w-full border-t border-gray-200"></div>
                        </div>
                        <div className="relative flex justify-center text-sm">
                            <span className="px-4 bg-gray-50 text-gray-500">or sign in with email</span>
                        </div>
                    </div>

                    <div className="bg-white rounded-2xl shadow-xl shadow-gray-200/50 p-8">
                        {error && (
                            <div className="mb-6 p-4 bg-red-50 border border-red-100 rounded-xl flex items-center gap-3 text-red-700">
                                <AlertCircle className="w-5 h-5 flex-shrink-0" />
                                <span className="text-sm">{error}</span>
                            </div>
                        )}

                        <form onSubmit={handleSubmit} className="space-y-5">
                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Email Address
                                </label>
                                <div className="relative group">
                                    <Mail className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400 group-focus-within:text-blue-500 transition-colors" />
                                    <input
                                        type="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        placeholder="name@example.com"
                                        required
                                        className="w-full pl-12 pr-4 py-3.5 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all text-gray-900 placeholder:text-gray-400"
                                    />
                                </div>
                            </div>

                            <div>
                                <label className="block text-sm font-semibold text-gray-700 mb-2">
                                    Password
                                </label>
                                <div className="relative group">
                                    <Lock className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400 group-focus-within:text-blue-500 transition-colors" />
                                    <input
                                        type={showPassword ? 'text' : 'password'}
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        placeholder="Enter your password"
                                        required
                                        className="w-full pl-12 pr-12 py-3.5 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all text-gray-900 placeholder:text-gray-400"
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowPassword(!showPassword)}
                                        className="absolute right-4 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600 transition-colors"
                                    >
                                        {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                                    </button>
                                </div>
                            </div>

                            <div className="flex items-center justify-between">
                                <label className="flex items-center gap-2 cursor-pointer">
                                    <input
                                        type="checkbox"
                                        className="w-4 h-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                                    />
                                    <span className="text-sm text-gray-600">Remember me</span>
                                </label>
                                <a href="#" className="text-sm text-blue-600 hover:text-blue-700 font-medium">
                                    Forgot password?
                                </a>
                            </div>

                            <button
                                type="submit"
                                disabled={loading}
                                className="w-full py-4 bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-xl font-semibold hover:from-blue-700 hover:to-blue-800 focus:ring-4 focus:ring-blue-300 transition-all disabled:opacity-70 disabled:cursor-not-allowed flex items-center justify-center gap-2 shadow-lg shadow-blue-500/30 hover:shadow-xl hover:shadow-blue-500/40 transform hover:-translate-y-0.5"
                            >
                                {loading ? (
                                    <>
                                        <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                                        <span>Signing in...</span>
                                    </>
                                ) : (
                                    <>
                                        <span>Sign In</span>
                                        <ArrowRight className="w-5 h-5" />
                                    </>
                                )}
                            </button>
                        </form>
                    </div>

                    <div className="mt-8 text-center">
                        <p className="text-sm text-gray-500">
                            Don't have an account?{' '}
                            <a href="#" className="text-blue-600 hover:text-blue-700 font-semibold">
                                Contact Admin
                            </a>
                        </p>
                        <p className="mt-4 text-xs text-gray-400">
                            Â© 2024 SmartEdu. All rights reserved.
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
