import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { Menu, X, Bell } from 'lucide-react';

const Navbar = ({ onMenuClick, isMobileMenuOpen }) => {
    const { user } = useAuth();
    const [showNotifications, setShowNotifications] = useState(false);

    return (
        <header className="bg-white/80 backdrop-blur-md shadow-sm h-16 fixed top-0 right-0 left-64 z-10 border-b border-slate-200/60">
            <div className="h-full px-6 flex items-center justify-between">
                {/* Mobile Menu Button */}
                <button
                    onClick={onMenuClick}
                    className="lg:hidden p-2 text-slate-600 hover:text-slate-900"
                >
                    {isMobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                </button>

                {/* Page Title */}
                <div className="hidden lg:block">
                    <h2 className="text-xl font-semibold text-slate-700">
                        Welcome back, {user?.name?.split(' ')[0]}!
                    </h2>
                </div>

                {/* Right Section */}
                <div className="flex items-center gap-4">
                    {/* Role Badge */}
                    <span className="px-3 py-1.5 rounded-full text-sm font-medium capitalize bg-gradient-to-r from-slate-100 to-slate-50 text-slate-700 border border-slate-200/60 shadow-sm">
                        {user?.role}
                    </span>

                    {/* Notifications */}
                    <div className="relative">
                        <button
                            onClick={() => setShowNotifications(!showNotifications)}
                            className="p-2 text-slate-600 hover:text-slate-900 hover:bg-slate-100 rounded-lg transition-colors relative"
                        >
                            <Bell className="w-5 h-5" />
                            <span className="absolute top-1 right-1 w-2 h-2 bg-emerald-500 rounded-full"></span>
                        </button>

                        {showNotifications && (
                            <div className="absolute right-0 mt-2 w-72 bg-white/95 backdrop-blur-md rounded-xl shadow-xl border border-slate-200/60 py-2">
                                <div className="px-4 py-2 border-b border-slate-100">
                                    <h3 className="font-semibold text-slate-800">Notifications</h3>
                                </div>
                                <div className="px-4 py-3 text-sm text-slate-500">
                                    No new notifications
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </header>
    );
};

export default Navbar;
