import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import {
    LayoutDashboard,
    Users,
    MessageSquare,
    LogOut,
    GraduationCap,
    UserCog,
    BookOpen
} from 'lucide-react';

const Sidebar = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = async () => {
        await logout();
        navigate('/login');
    };

    const getNavItems = () => {
        switch (user?.role) {
            case 'admin':
                return [
                    { path: '/admin', icon: LayoutDashboard, label: 'Dashboard' },
                    { path: '/admin/users', icon: Users, label: 'Manage Users' }
                ];
            case 'teacher':
                return [
                    { path: '/teacher', icon: LayoutDashboard, label: 'Dashboard' },
                    { path: '/teacher/students', icon: GraduationCap, label: 'My Students' }
                ];
            case 'student':
                return [
                    { path: '/student', icon: LayoutDashboard, label: 'Dashboard' },
                    { path: '/student/chatbot', icon: MessageSquare, label: 'AI Chatbot' }
                ];
            default:
                return [];
        }
    };

    const getRoleIcon = () => {
        switch (user?.role) {
            case 'admin':
                return <UserCog className="w-8 h-8 text-white" />;
            case 'teacher':
                return <BookOpen className="w-8 h-8 text-white" />;
            case 'student':
                return <GraduationCap className="w-8 h-8 text-white" />;
            default:
                return <Users className="w-8 h-8 text-white" />;
        }
    };

    const getRoleColor = () => {
        switch (user?.role) {
            case 'admin':
                return 'from-purple-600 to-purple-800';
            case 'teacher':
                return 'from-green-600 to-green-800';
            case 'student':
                return 'from-blue-600 to-blue-800';
            default:
                return 'from-gray-600 to-gray-800';
        }
    };

    const navItems = getNavItems();

    return (
        <aside className="w-64 bg-white shadow-lg h-screen fixed left-0 top-0 flex flex-col">
            {/* Logo Section */}
            <div className={`p-6 bg-gradient-to-r ${getRoleColor()}`}>
                <div className="flex items-center gap-3">
                    <div className="p-2 bg-white/20 rounded-lg">
                        {getRoleIcon()}
                    </div>
                    <div>
                        <h1 className="text-white font-bold text-lg">Smart Education</h1>
                        <p className="text-white/80 text-sm capitalize">{user?.role} Portal</p>
                    </div>
                </div>
            </div>

            {/* User Info */}
            <div className="p-4 border-b border-gray-200">
                <div className="flex items-center gap-3">
                    <div className={`w-10 h-10 rounded-full bg-gradient-to-r ${getRoleColor()} flex items-center justify-center text-white font-semibold`}>
                        {user?.name?.charAt(0).toUpperCase()}
                    </div>
                    <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-gray-900 truncate">{user?.name}</p>
                        <p className="text-xs text-gray-500 truncate">{user?.email}</p>
                    </div>
                </div>
            </div>

            {/* Navigation */}
            <nav className="flex-1 p-4 overflow-y-auto">
                <ul className="space-y-2">
                    {navItems.map((item) => (
                        <li key={item.path}>
                            <NavLink
                                to={item.path}
                                end={item.path === `/${user?.role}`}
                                className={({ isActive }) =>
                                    `flex items-center gap-3 px-4 py-3 rounded-lg transition-all duration-200 ${isActive
                                        ? `bg-gradient-to-r ${getRoleColor()} text-white shadow-md`
                                        : 'text-gray-600 hover:bg-gray-100'
                                    }`
                                }
                            >
                                <item.icon className="w-5 h-5" />
                                <span className="font-medium">{item.label}</span>
                            </NavLink>
                        </li>
                    ))}
                </ul>
            </nav>

            {/* Logout Button */}
            <div className="p-4 border-t border-gray-200">
                <button
                    onClick={handleLogout}
                    className="w-full flex items-center gap-3 px-4 py-3 text-red-600 hover:bg-red-50 rounded-lg transition-all duration-200"
                >
                    <LogOut className="w-5 h-5" />
                    <span className="font-medium">Logout</span>
                </button>
            </div>
        </aside>
    );
};

export default Sidebar;
